import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Contact.css';
import Navbar from '../Navbar'; // Import the Navbar component

const Contact = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        message: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle form submission logic here (e.g., send data to server)
        console.log('Form submitted:', formData);
        setFormData({ name: '', email: '', message: '' });
    };

    return (
        <div>
            <Navbar /> {/* Add the Navbar component */}
            <div className="header-banner">
                <h1>Let's Connect!</h1>
            </div>
            <div class="info-container">
                <div class="info-left">
                    <div class="info-box">
                        <h2 class="info-heading">Inquiries</h2>
                        <p class="info-content">For any inquiries, questions, or commendations, please send us an email.<br /><br />Email: <a href="mailto:alex@abc.com">alexia.leclercq@utexas.edu</a></p>                    
                        </div>
                    <div class="info-box">
                        <h2 class="info-heading">Mailing Location</h2>
                        <p class="info-content">We are located at: 123 Main St, Anytown, USA</p>
                    </div>
                </div>

                <div class="info-img">
                    <img src={`${process.env.PUBLIC_URL}/images/intro-img.JPG`} alt="Description of Image" class="contact-image" />

                </div>
            </div>
        </div>
    );
};

export default Contact;
