// Carousel.js

import React, { useState, useEffect } from 'react';

const CarouselImages = [
    { 
        src: `${process.env.PUBLIC_URL}/images/downtown-austin-skyline-copy.jpg`, 
        text: "Empower city departments and the community with climate data and tools", 
        overlayColor: 'rgba(0, 0, 0, 0.4)',
        isQuote: false 
    },
    { 
        src: `${process.env.PUBLIC_URL}/images/doggetposter.jpg`, 
        text: "Fostering collaboration between researchers, policymakers, and communities to develop innovative solutions that address climate challenges and build resilient urban environments.", 
        overlayColor: 'rgba(0, 0, 0, 0.4)',
        isQuote: true 
    },
    { 
        src: `${process.env.PUBLIC_URL}/images/another-image.jpg`, 
        text: `"Cities have the capability of providing something for everybody..."`, 
        overlayColor: 'rgba(0, 36, 122, 0.651)',
        isQuote: true 
    },
    // Add more images, texts, and colors here
];

const Carousel = () => {
    const [currentIndex, setCurrentIndex] = useState(0);

    const headerImages = CarouselImages.filter(image => !image.isQuote);
    const quoteImages = CarouselImages.filter(image => image.isQuote);
    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentIndex((prevIndex) => {
                const imagesToUse = isQuote ? quoteImages : headerImages;
                return prevIndex === imagesToUse.length - 1 ? 0 : prevIndex + 1;
            });
        }, 5000);

        return () => clearInterval(interval);
    }, [headerImages.length, quoteImages.length]);

    const goToNext = () => {
        const imagesToUse = isQuote ? quoteImages : headerImages;
        setCurrentIndex((prevIndex) =>
            prevIndex === imagesToUse.length - 1 ? 0 : prevIndex + 1
        );
    };

    const goToPrevious = () => {
        const imagesToUse = isQuote ? quoteImages : headerImages;
        setCurrentIndex((prevIndex) =>
            prevIndex === 0 ? imagesToUse.length - 1 : prevIndex - 1
        );
    };

    const isQuote = false; // Change to true for quote images

    const imagesToUse = isQuote ? quoteImages : headerImages;
    const currentImage = imagesToUse[currentIndex];

    return (
        <div className="carousel">
            <img src={currentImage.src} className="top-image" alt="Carousel" />
            <div className="overlay" style={{ backgroundColor: currentImage.isQuote ? currentImage.color : 'rgba(0, 0, 0, 0.4)' }}></div>
            <div className="text-overlay" style={{ color: currentImage.color }}>
                <h1>{currentImage.text}</h1>
            </div>
            <button className="caraousel-scroll-button left" onClick={goToPrevious}>
                &lt;
            </button>
            <button className="caraousel-scroll-button right" onClick={goToNext}>
                &gt;
            </button>
        </div>
    );
};

export default Carousel;
