import React, { useState, useEffect } from 'react';
import "./CoLab.css";
import Timeline from './Timeline';
import { Helmet } from 'react-helmet';
import NavbarHero from './NavbarHero';
import Navbar from './Navbar'


  const Headerimages = [
    { src: `${process.env.PUBLIC_URL}/images/downtown-austin-skyline-copy.jpg`, text: "Empower city departments and the community with climate data and tools", color: "white" },
    { src: `${process.env.PUBLIC_URL}/images/doggetposter.jpg`, text: "Fostering collaboration between researchers, policymakers, and communities to develop innovative solutions that address climate challenges and build resilient urban environments.", color: "white" },
    // Add more images, texts, and colors here
  ];

const timelineEvents = [
  { year: "2023", description: "Launch CoLab, Hire Staff, Governance Framework" },
  { year: "2024 Output 1", description: "Launch Climate Assessment Report" },
  { year: "2024 Output 2", description: "At least 1 decision workflow, Launch Climate Internship" },
  { year: "2024 Output 3", description: "At least 1 data product, modeling study, external funding & policy brief" },
  // Add more events here INCREASE OR DECREASE PADDING WHEN ADDING TO TIMELINE
];

const CoLab = () => {
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth <= 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex === Headerimages.length - 1 ? 0 : prevIndex + 1
      );
    }, 5000);

    return () => clearInterval(interval); // Cleanup on unmount
  }, []);

  const goToNextImage = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === Headerimages.length - 1 ? 0 : prevIndex + 1
    );
  };

  const goToPreviousImage = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? Headerimages.length - 1 : prevIndex - 1
    );
  };


  return (
    <div className="colab-main">
        <Helmet>
        <title>UT City CoLab</title>
        <meta property="og:type" content="website" />
        <meta property="og:url" content="https://texuslab.org/colab" />
        <meta property="og:title" content="UT City CoLab" />
        <meta property="og:description" content="Connecting climate data to Community + City decision making" />
        <meta property="og:image" content="https://texuslab.org/images/seal.svg" />


      </Helmet>

{/* ------------------------------------------------------------------------------------------*/}
{/* ------------------------------------------------------------------------------------------*/}
{/* ------------------------------------------------------------------------------------------*/}
{/* Header  */}

          <div className="colab-header">
              {isMobile ? <NavbarHero /> : <Navbar />}
              <div className="bannerimage-container">
          <img src={Headerimages[currentIndex].src} className="top-image" alt="Header" />
          <div className="overlay" style={{ backgroundColor: 'rgba(0, 0, 0, 0.5)' }}></div> {/* Black overlay */}
          <div className="text-overlay" style={{ color: Headerimages[currentIndex].color }}>
            <h1>{Headerimages[currentIndex].text}</h1>
          </div>
        </div>
              <button className="caraousel-scroll-button left" onClick={goToPreviousImage}>
                  &lt;
              </button>
              <button className="caraousel-scroll-button right" onClick={goToNextImage}>
                  &gt;
              </button>
          </div>

{/* ------------------------------------------------------------------------------------------*/}
{/* ------------------------------------------------------------------------------------------*/}
{/* ------------------------------------------------------------------------------------------*/}
{/* What is CoLab?  */}
          <div className="Introduction" id="target-section">
              <div className="intro-container">
                  <div class="introcover">
                      <img src={`${process.env.PUBLIC_URL}/images/intro-img.JPG`} />
                  </div>
                  <div class="intro-box">
                      <div>
                          <h1>What is <br />the <span style={{ color: '#B25321' }}>CoLab?</span></h1>
                      </div>
                      <div >
                          <p>The UT City & Climate CoLab is a collaborative initiative based at the University of Texas at Austin focused on addresing climate change and its impact on urban areas. It brings together researchers, policymakers, and community members to develop innovative solutions and strategies for building climate-resilient cities.</p>
                      </div>
                      <div class="btn">
                      <a href={`/about`} className="about-button" target="_blank" rel="noopener noreferrer">
                          <div className="about-button-content">Learn More</div>
                      </a>
                  </div>
                  </div>

              </div>

          </div>

{/* ------------------------------------------------------------------------------------------*/}
{/* ------------------------------------------------------------------------------------------*/}
{/* ------------------------------------------------------------------------------------------*/}
{/* Problems  */}

        <div className="Problems" id="target-section">
            {/* <img src={`${process.env.PUBLIC_URL}/images/intro-img.JPG`} alt="Background" className="prob-background" /> */}
            <div class="problem-content">
            <h1>Problems to Solve</h1>
            <div className="blob-section"> 
